CREATE TABLE IF NOT EXISTS nevjegyek (
	id smallint(5) unsigned NOT NULL AUTO_INCREMENT,
	foto varchar(64) NOT NULL DEFAULT 'nincskep.png',
	nev varchar(20) DEFAULT NULL,
	cegnev varchar(77) DEFAULT NULL,
	foglakozas varchar(60) DEFAULT NULL,
	email varchar(28) DEFAULT NULL,
	mobil varchar(13) DEFAULT NULL,
	PRIMARY KEY (id)
);

